import React from "react";
import './2048.css';

const emptyBoard = () =>[
    [1,0,0,0],
    [0,2,0,0],
    [0,0,3,0],
    [0,0,0,7]
];

const x = 0;
function Two048(){
    return(

    <div className="Container">
      <div className="Game">
      <div className="Header">
          <div className="Two048">2048</div>
          <div className="Scores-new">
            <div className="Scores">
              <div className="Score">
                <div>Score</div>
                <div>0</div>
              </div>
              <div className="Score">
                <div>Best</div>
                <div>0</div>
              </div>
            </div>
          <div className="New-game-button"><button className="Button">New Game</button></div>    
        </div>
        <div>{emptyBoard().forEach((element)=>{
          emptyBoard()[element][0];
        })}</div>           
      </div>

      <div className="Instructions">
        <p><b>HOW TO PLAY:</b> Use your <b>arrow keys</b> to move the tiles. Tiles</p>
        <p>with same number <b>merge into one</b> when they touch. Add</p>
        <p>them up to reach <b>2048!</b></p>
      </div>
    </div>
    </div>


    );
}

export default Two048;
