import { useContext, useState } from "react";
import EcommerceHome from "./EcommereceHome";
import ThemeContext, { themes } from "./ThemeContext";
import "./Ecommerce.css";

function Ecommerce() {
  const [theme, setTheme] = useState(themes.light);
  const [themeString, setThemeString] = useState("light");
  function toggleTheme() {
    if (theme === themes.dark) {
      setTheme(themes.light);
      setThemeString("container light");
    } else {
      setTheme(themes.dark);
      setThemeString("container dark");
    }
  }

  return (
    <ThemeContext.Provider value={theme}>
      <div className={themeString}>
        <button onClick={toggleTheme}>Click me to change theme.</button>
        <EcommerceHome></EcommerceHome>
      </div>
    </ThemeContext.Provider>
  );
}

export default Ecommerce;
