import { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ThemeContext from "./ThemeContext";
import "./Ecommerce.css";
import Product from "./Product";

function EcommerceHome() {
  const { theme, toggleTheme } = useContext(ThemeContext);
  const [homeProducts, setHomeProducts] = useState([]);
  const [filterOnPrice, setFilterOnPrice] = useState("");
  const [sortBy, setSortBy] = useState("sku");
  const [sortType, setSortType] = useState("asc");

  let url = `https://api.bestbuy.com/v1/products${filterOnPrice}?apiKey=HqLEjmjgDc84H5tGiiY78uGl&sort=${sortBy}.${sortType}&show=sku,image,name,salePrice&pageSize=5&format=json`;

  function filterPrice0to10() {
    setFilterOnPrice("(salePrice>=0&salePrice<=10)");
    console.log(filterOnPrice);
  }

  function filterPrice10to100() {
    setFilterOnPrice("(salePrice>=10&salePrice<=100)");
    console.log(filterOnPrice);
  }

  function filterPrice100to500() {
    setFilterOnPrice("(salePrice>=100&salePrice<=500)");
    console.log(filterOnPrice);
  }
  async function fetchProducts() {
    let response = await fetch(url);

    let data = await response.json();
    setHomeProducts(data.products);
  }

  useEffect(() => {
    console.log("hey");
    fetchProducts();
  }, [filterOnPrice, sortBy, sortType]);

  return (
    <div className={theme}>
      <div className="categories"> This is where product categories go. </div>
      <div className="filters-products">
        <div>
          {" "}
          FILTERS
          <div>
            <button onClick={filterPrice0to10}>$0 - $10</button>
            <br></br>
            <button onClick={filterPrice10to100}>$10 - $100</button>
            <br></br>
            <button>$100 - $500</button>
            <br></br>
            <button>{">"}$500</button>
          </div>
        </div>
        <div>
          {homeProducts.map((homeProduct) => {
            return (
              <Product key={homeProduct.sku} product={homeProduct}></Product>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default EcommerceHome;
