import { useState } from "react";
import { Link } from "react-router-dom";

function CartProduct(props) {
  const {
    product: { image, name, salePrice, sku, customerReviewAverage },
  } = props;

  const productsInCart = JSON.parse(localStorage.getItem("cart"));

  const currentProductIndex = productsInCart.findIndex(
    (element) => element.sku === sku
  );

  const [quantity, setQuantity] = useState(
    productsInCart[currentProductIndex].quantity
  );

  function handleTypeChange(event) {
    const newQuantity = event.target.value;
    console.log("hey");
    productsInCart[currentProductIndex].quantity = newQuantity;
    localStorage.setItem("cart", JSON.stringify(productsInCart));
    setQuantity(newQuantity);
  }

  function handleSubmit(event) {
    event.preventDefault();
  }

  function removeProductFromCart() {
    productsInCart.splice(currentProductIndex, 1);
    localStorage.setItem("cart", JSON.stringify(productsInCart));
  }
  return (
    <div>
      <img src={image}></img>
      <div>{sku}</div>
      <div>{name}</div>
      <div>{salePrice}</div>
      <div>Review - {customerReviewAverage}/5</div>
      <form onSubmit={handleSubmit}>
        <label>Quantity : </label>
        <input
          type="number"
          min="1"
          value={quantity}
          onChange={handleTypeChange}
        ></input>
      </form>
      <a href="/cart">
        <button onClick={removeProductFromCart}>
          Remove product from cart.
        </button>
      </a>
    </div>
  );
}

export default CartProduct;
