import { useState } from "react";

function CartProduct(props) {
  const {
    product: { image, name, salePrice, sku },
  } = props;

  const [quantity, setQuantity] = useState(1);
  return (
    <div>
      <img src={image}></img>
      <div>{sku}</div>
      <div>{name}</div>
      <div>{salePrice}</div>
      <form>
        <button>-</button>
        <input type="number" min="0" value={quantity}></input>
        <button>-</button>
      </form>
    </div>
  );
}

export default CartProduct;
