import { useState } from "react";

function CartProduct(props) {
  const {
    product: { image, name, salePrice, sku },
  } = props;

  const productsInCart = JSON.parse(localStorage.getItem("cart"));

  const currentProductIndex = productsInCart.findIndex(
    (element) => element.sku === sku
  );

  const [quantity, setQuantity] = useState(
    productsInCart[currentProductIndex].quantity
  );

  function handleTypeChange(event) {
    const newQuantity = event.target.value;
    console.log(currentProductIndex);
    if (newQuantity === 0) {
      productsInCart.splice(currentProductIndex, 1);
      console.log(productsInCart);
      localStorage.setItem("cart", JSON.stringify(productsInCart));
    } else {
      productsInCart[currentProductIndex].quantity = event.target.value;
      localStorage.setItem("cart", JSON.stringify(productsInCart));
      setQuantity(event.target.value);
    }
  }

  function handleSubmit(event) {
    event.preventDefault();
  }

  return (
    <div>
      <img src={image}></img>
      <div>{sku}</div>
      <div>{name}</div>
      <div>{salePrice}</div>
      <form onSubmit={handleSubmit}>
        <label>Quantity : </label>
        <input
          type="number"
          min="0"
          value={quantity}
          onChange={handleTypeChange}
        ></input>
      </form>
    </div>
  );
}

export default CartProduct;
