import { useContext } from "react";
import { Link } from "react-router-dom";
import ThemeContext from "./ThemeContext";
import { useState } from "react";

function EcommerceHome() {
  const [theme, setTheme] = useContext(ThemeContext);
  console.log(JSON.stringify(theme));

  const [themeString, setThemeString] = useState("light");
  function toggleTheme() {
    if (theme === themes.dark) {
      setTheme(themes.light);
      setThemeString("container light");
    } else {
      setTheme(themes.dark);
      setThemeString("container dark");
    }
  }
  return (
    <div>
      <button onClick={toggleTheme}>Click me to change theme.</button>
      Hello, welcome to my e-commerce website.
      <br></br>
      <br></br>
      <Link to="/cart">
        <button> Click me to go to a new page.</button>
      </Link>
    </div>
  );
}
