import { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ThemeContext from "./ThemeContext";
import "./Ecommerce.css";
import Product from "./Product";

function EcommerceHome() {
  const { theme, toggleTheme } = useContext(ThemeContext);
  const [homeProducts, setHomeProducts] = useState([]);

  async function fetchProducts() {
    let response = await fetch(
      "https://api.bestbuy.com/v1/products?apiKey=HqLEjmjgDc84H5tGiiY78uGl&sort=sku.asc&show=sku,image,name,salePrice&pageSize=5&format=json"
    );

    let data = await response.json();
    console.log(data.products);
    setHomeProducts(data.products);
  }

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className={theme}>
      <div className="categories"> This is where product categories go. </div>
      <div>
        {homeProducts.map((homeProduct) => {
          return (
            <Product key={homeProduct.sku} product={homeProduct}></Product>
          );
        })}
      </div>
    </div>
  );
}

export default EcommerceHome;
