import { useContext, useState } from "react";
import EcommerceHome from "./EcommereceHome";
import ThemeContext, { themes } from "./ThemeContext";

function Ecommerce() {
  const [theme, setTheme] = useState(themes.dark);
  const [themeString, setThemeString] = useState("light");
  function toggleTheme() {
    theme === themes.dark ? setTheme(themes.light) : setTheme(themes.dark);
  }

  return (
    <ThemeContext.Provider value={theme}>
      <div style={theme} className="container">
        <button onClick={toggleTheme}>Click me to change theme.</button>
        <EcommerceHome></EcommerceHome>
      </div>
    </ThemeContext.Provider>
  );
}

export default Ecommerce;
