import { createContext } from "react";

export const themes = {
  light: {
    color: "black",
    backgroundColor: "white",
  },

  dark: {
    color: "white",
    backgroundColor: "black",
  },
};

const ThemeContext = createContext(themes.light);

export default ThemeContext;
