  const theme = useContext(ThemeContext);

  //response.then(console.log(response.json()));

  useEffect(() => {
    const response = fetch(
      "https://api.bestbuy.com/v1/products((categoryPath.id=abcat0401000))?apiKey=HqLEjmjgDc84H5tGiiY78uGl&sort=sku.asc&show=image,sku,salePrice,name&pageSize=5&format=json"
    );
    // response.then(() => {
    //   console.log(response.json());
    // });
  }, []);
  return (
    <div className={theme}>
      Hello, welcome to my e-commerce website.
      <br></br>
      <br></br>
      <Link to="/cart">
        <button> Click me to go to a new page.</button>
      </Link>
    </div>
  );
}

export default EcommerceHome;
